import React, {useState, useEffect} from 'react'
import './css/Header.scss'
import { Link } from "react-scroll";
import { motion } from "framer-motion"
// import { scroll } from './hooks/useScroll.js';

function Header() {
    const [clicked, setClicked] = useState(false);
    const [scroll, setScroll] = useState(0);

    useEffect(() => {
        document.addEventListener("scroll", () => {
            const scrollCheck = window.scrollY;
            if (scrollCheck != scroll) {
                setScroll(scrollCheck);
            }
        })
    })

    return (
        <div className="header">
            <motion.div className="header__nav" initial={{ opacity: 0 }} animate={{ opacity: 1}} transition={{ delay: 2, duration: 1}}>
                <ul className="header__navList">
                    {/* <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="home"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className={((scroll > -1 && scroll < 550) ? 'nav__topic' : 'nav__topic')}>Home</div>
                        </Link>
                    </li> */}
                    <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="project"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className={((scroll > 350 && scroll < 1280) ? 'nav__current' : 'nav__topic')}>Projects</div>
                        </Link>
                    </li>
                    <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="about"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className={((scroll > 1280 && scroll < 2250) ? 'nav__current' : 'nav__topic')}>About</div>
                        </Link>                        
                    </li>
                    {/* <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="lab"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className={((scroll > 3000) ? 'nav__current' : 'nav__topic')}>Art</div>
                        </Link>                        
                    </li> */}
                    <li>
                        <input id="toggle" className="toggle" type="checkbox"></input>
                    </li>
                </ul>
                <div className="logo">
                    <a href="#"><div className="logo_text">Tarun Khasnavis.</div></a>
                </div>
            </motion.div>
        </div>
    )
}

export default Header
