import React, {useState, useEffect} from 'react'
import './css/Header.scss'
import { Link } from "react-scroll";
import { motion } from "framer-motion"
// import { scroll } from './hooks/useScroll.js';

function Header({ theme, toggle }) {
    const [clicked, setClicked] = useState(false);
    const [scroll, setScroll] = useState(0);

    useEffect(() => {
        document.addEventListener("scroll", () => {
            const scrollCheck = window.scrollY;
            if (scrollCheck != scroll) {
                setScroll(scrollCheck);
            }
        })
    })

    return (
        <div className="header">
            <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1, delay: 1.5 }}>
                <div className="navbar__mobile">
                    <div className="navbar__mobile__menuicon">
                    <span
                        className={clicked ? "fas fa-times" : "fas fa-bars"}
                        onClick={() => setClicked(!clicked)}
                    ></span>
                    <Link
                        activeClass="active"
                        to="splash"
                        spy={true}
                        offset={-30}
                        smooth={true}
                        duration={500}
                        onClick={() => setClicked(false)}
                    >
                        <motion.h1 className="navbar__mobile__title">Tarun Khasnavis.</motion.h1>
                    </Link>
                    <span
                        className={theme === "light" ? "fas fa-moon" : "fas fa-lightbulb"}
                        onClick={toggle}
                    ></span>
                    </div>
                </div>
            </motion.div>

            <motion.div className="header__nav" initial={{ opacity: 0 }} animate={{ opacity: 1}} transition={{ delay: 3, duration: 1}}>
                <ul className="header__navList">
                    {/* <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="home"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className={((scroll > -1 && scroll < 550) ? 'nav__topic' : 'nav__topic')}>Home</div>
                        </Link>
                    </li> */}
                    <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="project"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className={((scroll > 350 && scroll < 1280) ? 'nav__current' : 'nav__topic')}>Projects</div>
                        </Link>
                    </li>
                    <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="about"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className={((scroll > 1280 && scroll < 2250) ? 'nav__current' : 'nav__topic')}>About</div>
                        </Link>                        
                    </li>
                    {/* <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="lab"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className={((scroll > 3000) ? 'nav__current' : 'nav__topic')}>Art</div>
                        </Link>                        
                    </li> */}
                    <li>
                        <input id="toggle" className="toggle" type="checkbox" onClick={toggle}></input>
                    </li>
                </ul>
                <div className="logo">
                    <a href="#"><div className="logo_text">Tarun Khasnavis.</div></a>
                </div>
            </motion.div>
        </div>
    )
}

export default Header
