import React, {useState} from 'react';
import './css/App.scss';
import Project from './Project'
import Header from './Header'
// import Slideshow from './Slideshow'
// import 'react-slideshow-image/dist/styles.css'

function App() {

  const [MousePosition, setMousePosition] = useState({
    left: 50,
    top: 50,
  })

  function handleMouseMove(ev) { setMousePosition({left: ev.pageX, top: ev.pageY}); }

  return (
    <div className="app">
      <Header/>
      <div className="app__container">
        <div className="splash">
          <div className="bg"></div>
          <div className="title" onMouseMove={(ev)=> handleMouseMove(ev)} style={{left:MousePosition.left , top: MousePosition.top}}>
              <h1>Hello</h1>
              <p>I'm Tarun, an engineer & creator.</p>
          </div>
        </div>
        <Project/>
      </div>
    </div>
  );
}

export default App;
