import React, {useState, useEffect} from 'react'
import './css/Header.scss'
import { Link } from "react-scroll";
// import { scroll } from './hooks/useScroll.js';

function Header() {
    const [clicked, setClicked] = useState(false);
    const [scroll, setScroll] = useState(0);

    useEffect(() => {
        document.addEventListener("scroll", () => {
            const scrollCheck = window.scrollY;
            if (scrollCheck != scroll) {
                setScroll(scrollCheck);
            }
        })
    })

    return (
        <div className="header">
            <div className="header__nav">
                <ul className="header__navList"> 
                    <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="project"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className={(scroll > 1000 && scroll < 2000  ? 'nav_topic current' : 'nav_topic')}>Projects {"nav_topic " + (scroll < 100 ? 'current' : '')}</div>
                        </Link>
                    </li>
                    <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="about"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className="nav__topic">About</div>
                        </Link>                        
                    </li>
                    <li className="header__navItem">
                        <Link
                            className="nav__link"
                            activeClass="active"
                            to="lab"
                            spy={true}
                            onClick={() => setClicked(!clicked)}
                        >
                            <div className="nav__topic">Art</div>
                        </Link>                        
                    </li>
                    <li>
                        <input id="toggle" className="toggle" type="checkbox"></input>
                    </li>
                </ul>
            </div>
            <div className="logo">
                <div className="logo_text">TarunKhasnavis.</div>
            </div>
        </div>
    )
}

export default Header
